import React, { useState } from "react";

const HomePage: React.FC = () => {
  const [isPetting, setIsPetting] = useState(false);
  const [pettedEar, setPettedEar] = useState<"left" | "right" | null>(null);
  const [isHovered, setIsHovered] = useState(false);

  const handleHeadClick = () => {
    // Wywołaj efekt głaskania, tylko gdy nie trwa specjalna akcja uszu
    if (!isPetting && !pettedEar) {
      setIsPetting(true);
      setTimeout(() => {
        setIsPetting(false);
      }, 800);
    }
  };

  const handleEarClick = (ear: "left" | "right", e: React.MouseEvent<HTMLDivElement, MouseEvent>) => {
    e.stopPropagation();
    if (!pettedEar) {
      setPettedEar(ear);
      setTimeout(() => {
        setPettedEar(null);
      }, 800);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-4">
      <h1 className="text-3xl font-bold mb-6 text-gray-800">Głaskanie Kotka</h1>
      <div
        className={`relative cursor-pointer transition-transform duration-300 ${isHovered ? "scale-105" : "scale-100"}`}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        onClick={handleHeadClick}
      >
        {/* Główna głowa kotka */}
        <div className={`w-32 h-32 rounded-full bg-yellow-300 flex items-center justify-center relative ${isPetting ? "wiggle" : ""}`}>
          {/* Uszy */}
          <div
            className={`absolute -top-4 -left-4 w-10 h-10 rounded-full bg-yellow-300 ${pettedEar === "left" ? "ear-pet" : ""}`}
            onClick={(e) => handleEarClick("left", e)}
          />
          <div
            className={`absolute -top-4 -right-4 w-10 h-10 rounded-full bg-yellow-300 ${pettedEar === "right" ? "ear-pet" : ""}`}
            onClick={(e) => handleEarClick("right", e)}
          />
          {/* Prosta twarz - oczy */}
          <div className="flex space-x-4">
            <div className="w-2 h-2 bg-gray-800 rounded-full" />
            <div className="w-2 h-2 bg-gray-800 rounded-full" />
          </div>
        </div>
        {/* Ogon */}
        <div className="absolute -right-6 bottom-2 w-4 h-16 bg-yellow-300 rounded-full rotate-12"></div>
      </div>
      {(isPetting || pettedEar) && (
        <div className="mt-4 text-lg text-gray-700 animate-pulse">
          {pettedEar ? "Mrrr, uwielbiam uszy!" : "Prrrr..."}
        </div>
      )}
      <style jsx>{`
        @keyframes wiggle {
          0%, 100% { transform: rotate(0deg); }
          25% { transform: rotate(5deg); }
          75% { transform: rotate(-5deg); }
        }
        .wiggle {
          animation: wiggle 0.5s ease-in-out;
        }
        @keyframes earPet {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.3); }
        }
        .ear-pet {
          animation: earPet 0.5s ease-in-out;
        }
      `}</style>
    </div>
  );
};

export default HomePage;
